def encrypt(string, key):
    return b''.join([bytes(chr(ord(string[i]) ^ ord(key[i % len(key)])), 'utf-8') for i in range(len(string))]).hex()

def decrypt(ciphertext, key):
    return ''.join([chr(bytes.fromhex(ciphertext)[i] ^ ord(key[i % len(key)])) for i in range(len(bytes.fromhex(ciphertext)))])

